pragma solidity 0.8.28;

import "./enums/MortgageState.sol";
import "./interfaces/IMortgageToken.sol";
import "./storages/MortgageStorage.sol";
import "./storages/NFTStorage.sol";
import "./storages/TokenStorage.sol";
import "./utils/RepayInterestCalculator.sol";

contract MortgageToken is
    IMortgageToken,
    MortgageStorage,
    TokenStorage,
    NFTStorage,
    RepayInterestCalculator
{
    constructor(address _cryptoPawnToken) TokenStorage(_cryptoPawnToken) {}

    event CreateLoan(Mortgage mortgage);
    event Cancel(Mortgage mortgage);
    event Lend(Mortgage mortgage);
    event Repay(Mortgage mortgage);
    event Foreclose(Mortgage mortgage);

    function createLoan(
        uint _nftTokenId,
        address _nftContract,
        uint _principal,
        uint _interest,
        uint _duration
    ) external override returns (uint) {
        require(_nftContract != address(0), "Invalid contract address");

        require(_principal > 0, "Principal must be greater than 0");
        require(
            _principal <= 1000000,
            "Principal must be less than or equal to 1 million"
        );

        require(_duration > 0, "Duration must be greater than 0");
        require(
            _duration <= 365,
            "Duration must be less than or equal to 365 days"
        );
        require(
            _interest <= 100,
            "Interest must be less than or equal to 100%"
        );
        require(_interest > 0, "Interest must be greater than 0");

        mortgageId++;
        Mortgage memory mortgage = Mortgage(
            mortgageId,
            _nftTokenId,
            _principal,
            _interest,
            _duration,
            0,
            address(0),
            msg.sender,
            _nftContract,
            MortgageState.PENDING
        );
        mortgageIdToMortgage[mortgageId] = mortgage;
        emit CreateLoan(mortgage);
        return mortgageId;
    }

    function cancel(uint _mortgageId) external override {
        require(
            mortgageIdToMortgage[_mortgageId].borrower == msg.sender,
            "Not the borrower"
        );
        require(
            mortgageIdToMortgage[_mortgageId].state == MortgageState.PENDING,
            "Mortgage not in pending state"
        );
        mortgageIdToMortgage[_mortgageId].state = MortgageState.CANCELLED;
        emit Cancel(mortgageIdToMortgage[_mortgageId]);
    }

    function lend(uint _mortgageId) external override {
        require(
            mortgageIdToMortgage[_mortgageId].borrower != msg.sender,
            "Borrower cannot be lender"
        );
        require(
            mortgageIdToMortgage[_mortgageId].state == MortgageState.PENDING,
            "Mortgage not in pending state"
        );
        mortgageIdToMortgage[_mortgageId].lender = msg.sender;
        mortgageIdToMortgage[_mortgageId].state = MortgageState.SUPPLIED;
        mortgageIdToMortgage[_mortgageId].startTime = block.timestamp;
        _transferToken(
            msg.sender,
            mortgageIdToMortgage[_mortgageId].borrower,
            mortgageIdToMortgage[_mortgageId].principal
        );
        _depositNFT(
            _mortgageId,
            mortgageIdToMortgage[_mortgageId].nftContract,
            mortgageIdToMortgage[_mortgageId].nftTokenId,
            mortgageIdToMortgage[_mortgageId].borrower
        );
        emit Lend(mortgageIdToMortgage[_mortgageId]);
    }

    function repay(uint _mortgageId) external override {
        require(
            mortgageIdToMortgage[_mortgageId].borrower == msg.sender,
            "Not the borrower"
        );
        require(
            mortgageIdToMortgage[_mortgageId].state == MortgageState.SUPPLIED,
            "Mortgage not in supplied state"
        );
        require(
            mortgageIdToMortgage[_mortgageId].startTime +
                mortgageIdToMortgage[_mortgageId].duration >=
                block.timestamp,
            "Mortgage overdue"
        );

        // Use RepayInterestCalculator to calculate the repayment amount
        uint repaymentAmount = _calculateRepayAmount(
            mortgageIdToMortgage[_mortgageId].principal,
            mortgageIdToMortgage[_mortgageId].interest, // Convert to basis points
            mortgageIdToMortgage[_mortgageId].duration
        );

        require(
            cryptoPawnToken.balanceOf(msg.sender) >= repaymentAmount,
            "Insufficient funds for repayment"
        );

        require(
            cryptoPawnToken.allowance(msg.sender, address(this)) >=
                repaymentAmount,
            "Insufficient allowance"
        );

        _transferToken(
            msg.sender,
            mortgageIdToMortgage[_mortgageId].lender,
            repaymentAmount
        );

        _withdrawNFT(_mortgageId, msg.sender);

        mortgageIdToMortgage[_mortgageId].state = MortgageState.REPAID;
        emit Repay(mortgageIdToMortgage[_mortgageId]);
    }

    function foreclose(uint _mortgageId) external override {
        require(
            mortgageIdToMortgage[_mortgageId].lender == msg.sender,
            "Not the lender"
        );

        require(
            (block.timestamp >=
                mortgageIdToMortgage[_mortgageId].startTime +
                    mortgageIdToMortgage[_mortgageId].duration) ||
                (mortgageIdToMortgage[_mortgageId].state ==
                    MortgageState.REPAID),
            "Mortgage not overdue or already repaid"
        );

        _withdrawNFT(_mortgageId, msg.sender);

        mortgageIdToMortgage[_mortgageId].state = MortgageState.FORECLOSED;
        emit Foreclose(mortgageIdToMortgage[_mortgageId]);
    }

    function getMortgage(
        uint _mortgageId
    ) external view override returns (Mortgage memory) {
        return mortgageIdToMortgage[_mortgageId];
    }

    function getMortgagesByLender(
        address _lender
    ) external view override returns (Mortgage[] memory) {
        uint count = 0;
        for (uint i = 1; i <= mortgageId; i++) {
            if (mortgageIdToMortgage[i].lender == _lender) {
                count++;
            }
        }

        Mortgage[] memory mortgages = new Mortgage[](count);
        uint index = 0;
        for (uint i = 1; i <= mortgageId; i++) {
            if (mortgageIdToMortgage[i].lender == _lender) {
                mortgages[index] = mortgageIdToMortgage[i];
                index++;
            }
        }
        return mortgages;
    }

    function getMortgagesByBorrower(
        address _borrower
    ) external view override returns (Mortgage[] memory) {
        uint count = 0;
        for (uint i = 1; i <= mortgageId; i++) {
            if (mortgageIdToMortgage[i].borrower == _borrower) {
                count++;
            }
        }

        Mortgage[] memory mortgages = new Mortgage[](count);
        uint index = 0;
        for (uint i = 1; i <= mortgageId; i++) {
            if (mortgageIdToMortgage[i].borrower == _borrower) {
                mortgages[index] = mortgageIdToMortgage[i];
                index++;
            }
        }
        return mortgages;
    }

    function getAllMortgages()
        external
        view
        override
        returns (Mortgage[] memory)
    {
        Mortgage[] memory mortgages = new Mortgage[](mortgageId);
        for (uint i = 1; i <= mortgageId; i++) {
            mortgages[i - 1] = mortgageIdToMortgage[i];
        }
        return mortgages;
    }
}
