pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import "../structs/NFT.sol";

contract NFTStorage is IERC721Receiver {
    constructor() {}

    mapping(uint => NFT) storedNFTs;

    event Deposited(address indexed from, address indexed nft, uint256 tokenId);
    event Withdrawn(address indexed to, address indexed nft, uint256 tokenId);

    function _depositNFT(
        uint _mortgageId,
        address _nft,
        uint256 _tokenId,
        address _nftOwner
    ) internal {
        require(_mortgageId > 0, "Invalid mortgage ID");
        require(_nft != address(0), "Invalid contract address");

        IERC721(_nft).safeTransferFrom(_nftOwner, address(this), _tokenId);

        storedNFTs[_mortgageId] = NFT(_nft, _tokenId);

        emit Deposited(_nftOwner, _nft, _tokenId);
    }

    function _withdrawNFT(uint _mortgageId, address _to) internal {
        NFT memory nft = storedNFTs[_mortgageId];
        IERC721(nft.contractAddress).safeTransferFrom(
            address(this),
            _to,
            nft.tokenId
        );
        delete storedNFTs[_mortgageId];

        emit Withdrawn(_to, nft.contractAddress, nft.tokenId);
    }

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}
