contract TokenStorage {
    ICryptoPawnToken public cryptoPawnToken;

    constructor(address _cryptoPawnToken) {
        require(_cryptoPawnToken != address(0), "Invalid token address");
        cryptoPawnToken = ICryptoPawnToken(_cryptoPawnToken);
    }

    function _transferToken(
        address _from,
        address _to,
        uint256 _amount
    ) internal {
        require(_from != address(0), "Invalid sender address");
        require(_to != address(0), "Invalid receiver address");
        require(_amount > 0, "Amount must be greater than zero");
        require(
            _amount <= 1000000,
            "Amount must be less than or equal to 1 million"
        );
        require(
            cryptoPawnToken.balanceOf(_from) >= _amount,
