pragma solidity 0.8.28;

import {MulDiv} from "../libraries/MulDiv.sol";

contract RepayInterestCalculator {
    using MulDiv for uint256;

    uint256 internal constant PERCENTAGE_DENOMINATOR = 100_00; // 100.00% (2 decimals)

    /**
     * @notice Calculate the repay amount including interest
     * @param principal The loan principal amount
     * @param annualInterestRate Annual interest rate in basis points (e.g., 5% = 500)
     * @param durationInDays Duration of the loan in days
     * @return repayAmount Total amount to repay
     */
    function _calculateRepayAmount(
        uint256 principal,
        uint256 annualInterestRate,
        uint256 durationInDays
    ) internal pure returns (uint256 repayAmount) {
        // Convert annual interest rate to daily interest rate
        uint256 dailyInterestRate = annualInterestRate.mulDiv(1, 365); // Divide annual rate by 365 days

        // Calculate the interest amount
        uint256 interestAmount = principal
            .mulDiv(dailyInterestRate, PERCENTAGE_DENOMINATOR)
            .mulDiv(durationInDays, 1);

        // Total repay amount = principal + interest
        repayAmount = principal + interestAmount;
    }
}
